package control_flow_statements_looping_statements;

public class Do_while {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 0;
		do {
			System.out.println(a);
			a = a+2;
		} while(a<10);
//here statement 1st, then logic and then condition out side the do
			

	}

}
