package control_flow_statements_looping_statements;

public class For_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*looping statements are 4 types
		 * for loop, while loop, do-while loop, for each loop
		 */
		for (int a=0; a <5; a++) {
			System.out.println("Srujan");// srujan will be printed 5 times here
		}

	}

}
