package first_code;

public class Datatype1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a = 120;  // it will take upto 127(-128 to 127)
		short b = 2000; 
		int c = 20000;
		long d = 2000000;
		// upto here numericals datatypes
		boolean e = true;
		boolean f = false;
		float g = 10.5f;  // for decimal values and after decimal points mention f at the end
		double h = 10.999999;
		char i = 's'; // single quotes for single char and double quotes is used for strings
		System.out.println(a);
		System.out.println(b);
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
		System.out.println(f);
		System.out.println(g);
		System.out.println(h);
		System.out.println(i);



	}

}
