package operators;

public class Arthematic_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 100;
		int b = 300;
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);
		

	}

}
