package strings;

public class string_charAt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//we can retrieve the string value by using an index number
		String lev = "Levitica";
		System.out.println(lev.charAt(3));
//here index no starts with 0 and given 3 so that 4th letter i is printed
	}

}
